import React from 'react'
import { useFormik } from 'formik';
import { addpro } from './functionPro'
import { MDBContainer, MDBRow, MDBCol, MDBBtn, MDBInput } from 'mdbreact';


  const AddPortfolio = (props) => {
    const formik = useFormik({
      initialValues: {
        education:'',
        img: '',
        jobPosition: '',
        brandstatment: '',
        github: '',
        linkdin: '',
        twitter: '',
        role:1
      },
      onSubmit: async (values) => {



        // console.log(values)

        addpro(values)
          .then(r => {
            console.log(r)
            //1 means email is in use
            if(r.data.msg=="1"){
              console.log("1")
              alert("Email is already in use")
            }else if(r.data.msg=="0"){
              console.log("0")
              alert("username is already in use")
            }else if(r.data.msg=="3"){
              console.log("3")
              console.log(r.data.user)
              props.toggleSub(r.data.user)
              //redirect to the add portfolio and send the user id with it
              alert("User registered!")
            }
          }
          )
          .catch(err => console.log(err))
        // alert(JSON.stringify(values, null, 2));
      },
    });

  return (
    <MDBContainer>

    <p>Developer</p>
    <MDBRow>
      <MDBCol md="6">
        <form onSubmit={formik.handleSubmit}>
          <MDBInput
            label="your Img"
            id="Img"
            name="Img"
            type="text"
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
            value={formik.values.education}
          />
          {formik.touched.firstname && formik.errors.firstname ? (
            <div>{formik.errors.firstname}</div>
          ) : null}

          <MDBInput
            label="Last name"
            id="lastname"
            name="lastname"
            type="text"
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
            value={formik.values.lastname}
          />
          {formik.touched.lastname && formik.errors.lastname ? (
            <div>{formik.errors.lastname}</div>
          ) : null}

          <MDBInput
            label="Username"
            id="username"
            name="username"
            type="text"
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
            value={formik.values.username}
          />
          {formik.touched.username && formik.errors.username ? (
            <div>{formik.errors.username}</div>
          ) : null}

          <MDBInput
            label="Email"
            id="email"
            name="email"
            type="email"
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
            value={formik.values.email}
          />
          {formik.touched.email && formik.errors.email ? (
            <div>{formik.errors.email}</div>
          ) : null}
          <MDBInput
            label="Password"
            id="password"
            name="password"
            type="password"
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
            value={formik.values.password}
          />
          {formik.touched.password && formik.errors.password ? (
            <div>{formik.errors.password}</div>
          ) : null}

          <MDBInput
            label="Confirm password"
            id="cpassword"
            name="cpassword"
            type="password"
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
            value={formik.values.cpassword}
          />
          {formik.touched.cpassword && formik.errors.cpassword ? (
            <div>{formik.errors.cpassword}</div>
          ) : null}

          <MDBBtn type="submit" color="primary">
            {/* <Link to = "/addPortfolio" > Register </Link> */}
            </MDBBtn>
          {/* <Button type="submit">Submit</Button> */}
        </form>
      </MDBCol>
    </MDBRow>
  </MDBContainer>
  )
};

export default AddPortfolio
