import React, { useState ,  useRef, useEffect } from 'react'
import {Link} from "react-router-dom"
import pro from "../Images/pro.png"
import "../App.css"
import Linkedin from "../Images/linkedin.svg"
import youtube from "../Images/youtube.svg"
import twitter from "../Images/twitter.svg"
import facebook from "../Images/facebook.svg"
import clsx from 'clsx';
import { makeStyles} from '@material-ui/core/styles';
import Drawer from '@material-ui/core/Drawer';
import Toolbar from '@material-ui/core/Toolbar';
import Github from "../Images/github.png"
import List from '@material-ui/core/List';
import Divider from '@material-ui/core/Divider';
import Typical from 'react-typical'
import { Figure} from 'react-bootstrap';
import UserProject from "../Components/userProject"
import axios from 'axios';

import {localhost} from "../GlobalVars"

const scrollToRef = (ref) => window.scrollTo(0, ref.current.offsetTop)   
//const useMountEffect = (fun) => useEffect(fun, [])

const drawerWidth = 480;
const useStyles = makeStyles(theme => ({ drawerPaper: {width: drawerWidth,},}));

export default function Portfolio (props) {


  const [userInfo, setUserInfo] = useState([]);

  useEffect(async() => {
    // const result = await
    await axios.get(`${localhost}/user/username/jkt`)
      .then(result => {
        console.log("user")
        console.log(result)
        console.log(result.data._id)
        axios.get(`${localhost}/UserInfoRoutes/${result.data._id}`)
        .then(rr=>console.log(rr))
        // console.log("LLLLLLLL")
        // // console.log(result)
        // console.log("data")
        // console.log(result.data[0].user.firstname)
        // console.log(result.data[0])
        // setUserInfo(result.data[0])

      });
  }, 
  []);



  const myRef = useRef(null)

  const [showText, setShowText] = useState(false);

  const classes = useStyles();
  const [open, setOpen] = React.useState(false);
  const handleDrawerOpen = () => {setOpen(true);};
  const handleDrawerClose = () => {setOpen(false);};  


  // console.log(props.user_id);

    // console.log(props.data);

    console.log(userInfo)

    // console.log(props)
    // console.log(props.location.state.data)
        return (
         <div>  

{ userInfo.length!=0 &&  <div className="prot">


                <img classNmae="prtimgUser" src={pro} alt="Portfolio" /> 
                <Typical className="textStyle" steps={['Hello', 1000, "Hello I'm", 5000]}  loop={Infinity}  wrapper="p" />
                <p className="textStyle1">{userInfo.user.firstname} {userInfo.user.lastname}</p> 
                <p className="textStyle2">
                {userInfo.jobPosition}
                  </p> 

        <p className="textStyle3">

          {userInfo.brandstatment} 

          </p> 
                 <div className="aStyle">


                 <Link to = "/"> <img   src={Github} alt="boy" /> </Link>
                 <Link to = "/"> <img   src={Linkedin} alt="boy" /> </Link>
                 <Link to = "/"> <img   src={youtube} alt="boy" /> </Link>
                 <Link to = "/"> <img   src={twitter} alt="boy" /> </Link>
                 <Link to = "/"> <img   src={facebook} alt="boy" /> </Link>

                 </div>


        <div className="profil">
         <Toolbar  className="tStyle" >
         {/* setShowText(!showText) */}
  <Link   onClick={handleDrawerOpen} className={clsx( "profileLink")} >About Me  | </Link>

           <Link   className ="profileLink" onClick={() => {setShowText(!showText) 
            setTimeout(() => {
              if (!showText) 
              {scrollToRef(myRef)}
            }, 500)  }} >  My Projects </Link>


        </Toolbar>
        </div>



<main className={clsx(classes.content, { [classes.contentShift]: open,})} >
  <div />
</main>
      <Drawer

        variant="persistent"
        anchor="right"
        open={open}
        classes={{ paper: classes.drawerPaper, }} >
        <div >
         <h1 onClick={handleDrawerClose} className="goBack" >  {"<"} </h1>
        </div>

        <List>
          {/* {['Inbox', 'Starred', 'Send email', 'Drafts'].map((text, index) => (
              <ListItemText primary={text} />
          ))} */}

           <Figure.Image  className="imgProfile" width={171} height={180} alt="171x180" src={pro}   roundedCircle   /> <br/>
          <Divider />
          <p className="tSpacing">Education : </p>
          <br/>
          <p className="tSpacing"> {userInfo.brandstatment}  </p>

          <Divider />
        </List>
      </Drawer>

  </div>}



  {/* showText */}
         {showText &&


        <div ref={myRef} >
          <div className="projectCards"> 
            {props.data.map( e =>{ 
          return <UserProject  ele ={e} />})}  
          </div>
          </div>} 

  </div>
        );

}
